function trocar_imagem(id){imagem_destaque=document.getElementById('image_destaque')
imagem_destaque.src=id.src}
function mover_imagem(id){id.classList.add("zoom");x=id.pageX,y=id.pageY;limite=25;}
$(window).load=function(){$("#distorcao").css({transform:"translate(-50%,-50%) rotateX(0deg) rotateY(0deg) translateZ(0)"});}
$(document).on("mousemove",function(e){x=e.pageX,y=e.pageY;limite=25;var ww=window.innerWidth||document.documentElement.clientWidth||document.body.clientWidth;var wh=window.innerHeight||document.documentElement.clientHeight||document.body.clientHeight;console.log(ww,wh)
rotx=y*100/wh;rotx=180*rotx/100;rotx=rotx-90;rotx*=-1;roty=x*100/ww;roty=180*roty/100;roty=roty-90;$("#distorcao").css({transform:"translate(-50%,-50%) rotateX("+(roty*limite/100)+"deg) rotateY("+(rotx*limite/100)+"deg) translateZ(0)"});});